import React, { useState, useEffect } from 'react';

function Admin() {
    const [transactions, setTransactions] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchTransactions = async () => {
            try {
                const data = await handleUpdateTransactions({});
                setTransactions(data);
            } catch (error) {
                console.error('Failed to fetch transactions:', error);
                setError('Failed to fetch transactions');
            }
        };

        fetchTransactions();
    }, []);

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="AppContainer">
            <h1>Transactions</h1>
            {transactions.length > 0 ? (
                transactions.map((transaction, index) => (
                    <div key={index} className="List">
                        <div>User ID: {transaction.user_id}</div>
                        <div>File ID: {transaction.file_id}</div>
                        <div>Timestamp: {new Date(transaction.timeStamp).toLocaleString()}</div>
                        <div>Uploaded: {transaction.up ? "Yes" : "No"}</div>
                    </div>
                ))
            ) : (
                <div>No transactions found.</div>
            )}
        </div>
    );
}

export default Admin;
